import React from 'react';
import Modal from 'react-modal';
import './authmodal.css';

Modal.setAppElement('#root'); // For accessibility

const AuthModal = ({ isOpen, onClose, selectedForm }) => {
  const isLoginForm = selectedForm === 'login';

  return (
    <Modal className='modal_card' isOpen={isOpen} onRequestClose={onClose}>
      <button onClick={onClose} className="close-button">
        <span aria-hidden="true">&times;</span>
      </button>

      <div className="form-container">
        <div className="form">
        <h3  className='form-heading'>{isLoginForm ? '' : ''}Cryp<span className='primary'>to</span></h3>
          <form>
            <div className="form-content">
              {/* {isLoginForm ?  : null} */}
              <input type="email" placeholder="Email" />
              <input type="password" placeholder="Password" />
              {isLoginForm ? <p className="forgot-password">Forgot password?</p> : null}
              {isLoginForm ? <p>or</p> : null}
             <button className='btn'>{isLoginForm ? 'Sign in with Google': 'Sign up with Google'}</button> 
              <button className='btn'>{isLoginForm ? 'Login' : 'Register'}</button>
            </div>
          </form>
        </div>
      </div>
    </Modal>
  );
};

export default AuthModal;
